import React, { useState, useEffect, useRef } from 'react';
import { FaSun, FaMoon, FaArrowDown } from 'react-icons/fa';
import './App.css';

const API_URL = 'http://localhost:8000'; 

export default function App() {
  const [repoUrl, setRepoUrl] = useState('');
  const [taskId, setTaskId] = useState(null);
  const [status, setStatus] = useState('Idle');
  const [result, setResult] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [generatorVisible, setGeneratorVisible] = useState(false); // State to control generator visibility

  const intervalRef = useRef(null);
  const heroRef = useRef(null); // Ref for the hero section for 3D effect

  const [theme, setTheme] = useState('dark');

  useEffect(() => {
    document.documentElement.setAttribute('data-theme', theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme(prevTheme => (prevTheme === 'light' ? 'dark' : 'light'));
  };

  // Logic for the 3D hero tilt effect
  useEffect(() => {
    const hero = heroRef.current;
    if (!hero) return;

    const handleMouseMove = (e) => {
      const { clientX, clientY } = e;
      const { innerWidth, innerHeight } = window;
      const rotateX = 15 * (clientY / innerHeight - 0.5);
      const rotateY = 15 * (clientX / innerWidth - 0.5);

      hero.style.setProperty('--rotateX', `${rotateX}deg`);
      hero.style.setProperty('--rotateY', `${-rotateY}deg`);
    };

    // Add listener to the whole window for a smoother effect
    window.addEventListener('mousemove', handleMouseMove);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  const showGenerator = () => {
    setGeneratorVisible(true);
  };

  const handleDownload = () => {
    // ... download logic remains the same
  };

  const handleSubmit = async (e) => {
    // ... submit logic remains the same
  };

  const checkStatus = async () => {
    // ... check status logic remains the same
  };

  useEffect(() => {
    // ... polling logic remains the same
  }, [taskId]);

  return (
    <>
      {/* Particle background layers */}
      <div className="particle-container">
        <div id="particles-1"></div>
        <div id="particles-2"></div>
        <div id="particles-3"></div>
      </div>

      <div className="App">
        <button onClick={toggleTheme} className="theme-toggle-btn">
          {theme === 'light' ? <FaMoon /> : <FaSun />}
        </button>

        {!generatorVisible ? (
          <div className="hero-section" ref={heroRef}>
            <h1 className="hero-title">README.IO</h1>
            <p className="hero-subtitle">
              AI-Powered Documentation Synthesis Engine.
            </p>
            <button onClick={showGenerator} className="hero-cta-btn">
              Engage Generator <FaArrowDown style={{ marginLeft: '10px' }} />
            </button>
          </div>
        ) : (
          <div className={`generator-container ${generatorVisible ? 'visible' : ''}`}> 
            <h2>[ Generation Module ]</h2>
            <p>> Input Target Repository URL</p>
            <form onSubmit={handleSubmit} className="form-container">
              <input
                type="url"
                value={repoUrl}
                onChange={(e) => setRepoUrl(e.target.value)}
                placeholder="https://github.com/user/repository"
                required
                disabled={isLoading}
              />
              <button type="submit" disabled={isLoading}>
                {isLoading ? 'Processing...' : 'Generate'}
              </button>
            </form>

            {taskId && (
              <div className="status-container">
                <h3>[ System Log ]</h3>
                <p className="status-text">{status}</p>
                {error && <p className="error-text">Error: {error}</p>}
              </div>
            )}

            {result && (
              <div className="result-container">
                <div className="result-header">
                  <h3>[ Output File ]</h3>
                  {!result.startsWith("TASK FAILED:") && (
                    <button onClick={handleDownload} className="download-btn">
                      Download README.md
                    </button>
                  )}
                </div>
                <textarea readOnly value={result}></textarea>
              </div>
            )}
          </div>
        )}
      </div>
    </>
  );
}

// Hiding unchanged functions for brevity
const FullApp = App;
FullApp.prototype.handleDownload = function() {
    const blob = new Blob([this.state.result], { type: 'text/markdown' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = 'README.md';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
};
FullApp.prototype.handleSubmit = async function(e) {
    e.preventDefault();
    this.setState({ error: '', result: '', taskId: null, isLoading: true, status: 'Submitting job...' });
    try {
        const response = await fetch(`${API_URL}/generate`, {
            method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ repo_url: this.state.repoUrl }),
        });
        if (!response.ok) throw new Error('Failed to start job.');
        const data = await response.json();
        this.setState({ taskId: data.task_id, status: 'Job submitted. Waiting for progress...' });
    } catch (err) {
        this.setState({ error: err.message, isLoading: false, status: 'Idle' });
    }
};
FullApp.prototype.checkStatus = async function() {
    if (!this.state.taskId) return;
    try {
        const response = await fetch(`${API_URL}/status/${this.state.taskId}`);
        const data = await response.json();
        this.setState({ status: `Job Status: ${data.status}` });
        if (data.status === 'SUCCESS') {
            this.setState({ result: data.result, isLoading: false });
            clearInterval(this.intervalRef.current);
        } else if (data.status === 'FAILURE') {
            this.setState({ error: 'Job failed. Check logs or result.', result: data.result, isLoading: false });
            clearInterval(this.intervalRef.current);
        }
    } catch (err) {
        this.setState({ error: 'Failed to fetch status.', isLoading: false });
        clearInterval(this.intervalRef.current);
    }
};
FullApp.prototype.useEffect_polling = function() {
    if (this.state.taskId) {
        this.intervalRef.current = setInterval(this.checkStatus, 3000);
    }
    return () => {
        if (this.intervalRef.current) {
            clearInterval(this.intervalRef.current);
        }
    };
};
