import React, { useState, useEffect, useRef } from 'react';
import { FaSun, FaMoon, FaArrowDown } from 'react-icons/fa';
import './App.css';

const API_URL = 'http://localhost:8000'; 

function App() {
  const [repoUrl, setRepoUrl] = useState('');
  const [taskId, setTaskId] = useState(null);
  const [status, setStatus] = useState('Idle');
  const [result, setResult] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const intervalRef = useRef(null);
  const generatorRef = useRef(null); // Ref for the generator section

  const [theme, setTheme] = useState('dark');

  useEffect(() => {
    document.documentElement.setAttribute('data-theme', theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme(prevTheme => (prevTheme === 'light' ? 'dark' : 'light'));
  };

  // Function to smoothly scroll to the generator
  const scrollToGenerator = () => {
    generatorRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleDownload = () => {
    const blob = new Blob([result], { type: 'text/markdown' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = 'README.md';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setResult('');
    setTaskId(null);
    setIsLoading(true);
    setStatus('Submitting job...');

    try {
      const response = await fetch(`${API_URL}/generate`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ repo_url: repoUrl }),
      });
      if (!response.ok) throw new Error('Failed to start job.');
      const data = await response.json();
      setTaskId(data.task_id);
      setStatus('Job submitted. Waiting for progress...');
    } catch (err) {
      setError(err.message);
      setIsLoading(false);
      setStatus('Idle');
    }
  };

  const checkStatus = async () => {
    if (!taskId) return;
    try {
      const response = await fetch(`${API_URL}/status/${taskId}`);
      const data = await response.json();
      setStatus(`Job Status: ${data.status}`);
      if (data.status === 'SUCCESS') {
        setResult(data.result);
        setIsLoading(false);
        clearInterval(intervalRef.current);
      } else if (data.status === 'FAILURE') {
        setError('Job failed. Check logs or result.');
        setResult(data.result); 
        setIsLoading(false);
        clearInterval(intervalRef.current);
      }
    } catch (err) {
      setError('Failed to fetch status.');
      setIsLoading(false);
      clearInterval(intervalRef.current);
    }
  };

  useEffect(() => {
    if (taskId) {
      intervalRef.current = setInterval(checkStatus, 3000);
    }
    return () => clearInterval(intervalRef.current);
  }, [taskId]);

  return (
    <div className="App">
      <button onClick={toggleTheme} className="theme-toggle-btn">
        {theme === 'light' ? <FaMoon /> : <FaSun />}
      </button>

      {/* --- NEW: Hero Landing Page Section --- */}
      <div className="hero-section">
        <h1 className="hero-title">Intelligent READMEs, Instantly.</h1>
        <p className="hero-subtitle">
          Stop writing documentation from scratch. Our AI-powered generator analyzes your GitHub repository and creates a high-quality, context-aware README.md in seconds.
        </p>
        <button onClick={scrollToGenerator} className="hero-cta-btn">
          Start Generating <FaArrowDown style={{ marginLeft: '10px' }} />
        </button>
      </div>

      {/* --- Main Generator Section --- */}
      <div id="generator" ref={generatorRef}>
        <div className="generator-container">
          <h2>Let's Get Started</h2>
          <p>Paste the URL of a public GitHub repository below.</p>
          <form onSubmit={handleSubmit} className="form-container">
            <input
              type="url"
              value={repoUrl}
              onChange={(e) => setRepoUrl(e.target.value)}
              placeholder="https://github.com/user/repo"
              required
              disabled={isLoading}
            />
            <button type="submit" disabled={isLoading}>
              {isLoading ? 'Generating...' : 'Generate'}
            </button>
          </form>

          {taskId && (
            <div className="status-container">
              <h3>Status</h3>
              <p className="status-text">{status}</p>
              {error && <p className="error-text">Error: {error}</p>}
            </div>
          )}

          {result && (
            <div className="result-container">
              <div className="result-header">
                <h3>Generated Content</h3>
                {!result.startsWith("TASK FAILED:") && (
                  <button onClick={handleDownload} className="download-btn">
                    Download README.md
                  </button>
                )}
              </div>
              <textarea readOnly value={result}></textarea>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
