import React, { useState, useEffect, useRef } from 'react';
import { FaSun, FaMoon, FaArrowDown } from 'react-icons/fa';
import './App.css';

const API_URL = 'http://localhost:8000'; 

export default function App() {
  const [repoUrl, setRepoUrl] = useState('');
  const [taskId, setTaskId] = useState(null);
  const [status, setStatus] = useState('Idle');
  const [result, setResult] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const intervalRef = useRef(null);
  const generatorRef = useRef(null);
  const cardRef = useRef(null); // Ref for the 3D card

  const [theme, setTheme] = useState('dark');

  useEffect(() => {
    document.documentElement.setAttribute('data-theme', theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme(prevTheme => (prevTheme === 'light' ? 'dark' : 'light'));
  };

  // Logic for the 3D card tilt effect
  useEffect(() => {
    const card = cardRef.current;
    if (!card) return;

    const handleMouseMove = (e) => {
      const { clientX, clientY, currentTarget } = e;
      const { left, top, width, height } = currentTarget.getBoundingClientRect();
      const x = clientX - left;
      const y = clientY - top;
      // Adjust divisor for sensitivity
      const rotateX = 10 * (y - height / 2) / (height / 2);
      const rotateY = -10 * (x - width / 2) / (width / 2);

      card.style.setProperty('--rotateX', `${rotateX}deg`);
      card.style.setProperty('--rotateY', `${rotateY}deg`);
    };

    const handleMouseLeave = () => {
      card.style.setProperty('--rotateX', '0deg');
      card.style.setProperty('--rotateY', '0deg');
    };

    card.addEventListener('mousemove', handleMouseMove);
    card.addEventListener('mouseleave', handleMouseLeave);

    return () => {
      card.removeEventListener('mousemove', handleMouseMove);
      card.removeEventListener('mouseleave', handleMouseLeave);
    };
  }, []);


  const scrollToGenerator = () => {
    generatorRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleDownload = () => {
    const blob = new Blob([result], { type: 'text/markdown' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = 'README.md';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setResult('');
    setTaskId(null);
    setIsLoading(true);
    setStatus('Submitting job...');

    try {
      const response = await fetch(`${API_URL}/generate`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ repo_url: repoUrl }),
      });
      if (!response.ok) throw new Error('Failed to start job.');
      const data = await response.json();
      setTaskId(data.task_id);
      setStatus('Job submitted. Waiting for progress...');
    } catch (err) {
      setError(err.message);
      setIsLoading(false);
      setStatus('Idle');
    }
  };

  const checkStatus = async () => {
    if (!taskId) return;
    try {
      const response = await fetch(`${API_URL}/status/${taskId}`);
      const data = await response.json();
      setStatus(`Job Status: ${data.status}`);
      if (data.status === 'SUCCESS') {
        setResult(data.result);
        setIsLoading(false);
        clearInterval(intervalRef.current);
      } else if (data.status === 'FAILURE') {
        setError('Job failed. Check logs or result.');
        setResult(data.result); 
        setIsLoading(false);
        clearInterval(intervalRef.current);
      }
    } catch (err) {
      setError('Failed to fetch status.');
      setIsLoading(false);
      clearInterval(intervalRef.current);
    }
  };

  useEffect(() => {
    if (taskId) {
      intervalRef.current = setInterval(checkStatus, 3000);
    }
    return () => {
        if(intervalRef.current) {
            clearInterval(intervalRef.current);
        }
    };
  }, [taskId]);

  return (
    <div className="App">
      <button onClick={toggleTheme} className="theme-toggle-btn">
        {theme === 'light' ? <FaMoon /> : <FaSun />}
      </button>

      <div className="hero-section">
        <h1 className="hero-title">AI-Powered README Generator</h1>
        <p className="hero-subtitle">
          Create beautiful, context-aware documentation for your code in seconds. Let AI handle the writing.
        </p>
        <button onClick={scrollToGenerator} className="hero-cta-btn">
          Get Started
        </button>
      </div>

      <div id="generator" ref={generatorRef}>
        <div className="generator-container" ref={cardRef}> 
          <h2>Generation Engine</h2>
          <p>Enter a public GitHub repository URL to begin.</p>
          <form onSubmit={handleSubmit} className="form-container">
            <input
              type="url"
              value={repoUrl}
              onChange={(e) => setRepoUrl(e.target.value)}
              placeholder="https://github.com/user/repository"
              required
              disabled={isLoading}
            />
            <button type="submit" disabled={isLoading}>
              {isLoading ? 'Generating...' : 'Generate'}
            </button>
          </form>

          {taskId && (
            <div className="status-container">
              <h3>Status</h3>
              <p className="status-text">{status}</p>
              {error && <p className="error-text">Error: {error}</p>}
            </div>
          )}

          {result && (
            <div className="result-container">
              <div className="result-header">
                <h3>Generated README</h3>
                {!result.startsWith("TASK FAILED:") && (
                  <button onClick={handleDownload} className="download-btn">
                    Download README.md
                  </button>
                )}
              </div>
              <textarea readOnly value={result}></textarea>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
