import React, { useState, useEffect, useRef } from 'react';
import { FaSun, FaMoon, FaArrowDown } from 'react-icons/fa';
import './App.css';

const API_URL = 'http://localhost:8000'; 

function App() {
  const [repoUrl, setRepoUrl] = useState('');
  const [taskId, setTaskId] = useState(null);
  const [status, setStatus] = useState('Idle');
  const [result, setResult] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const intervalRef = useRef(null);
  const generatorRef = useRef(null);
  const cardRef = useRef(null); // Ref for the generator card for 3D effect

  const [theme, setTheme] = useState('dark');

  useEffect(() => {
    document.documentElement.setAttribute('data-theme', theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme(prevTheme => (prevTheme === 'light' ? 'dark' : 'light'));
  };

  // --- NEW: Logic for 3D card tilt effect ---
  useEffect(() => {
    const card = cardRef.current;
    if (!card) return;

    const handleMouseMove = (e) => {
      const { clientX, clientY, currentTarget } = e;
      const { left, top, width, height } = currentTarget.getBoundingClientRect();
      const x = clientX - left;
      const y = clientY - top;
      const rotateX = 20 * (y - height / 2) / (height / 2);
      const rotateY = -20 * (x - width / 2) / (width / 2);

      card.style.setProperty('--rotateX', `${rotateX}deg`);
      card.style.setProperty('--rotateY', `${rotateY}deg`);
    };

    const handleMouseLeave = () => {
      card.style.setProperty('--rotateX', '0deg');
      card.style.setProperty('--rotateY', '0deg');
    };

    card.addEventListener('mousemove', handleMouseMove);
    card.addEventListener('mouseleave', handleMouseLeave);

    return () => {
      card.removeEventListener('mousemove', handleMouseMove);
      card.removeEventListener('mouseleave', handleMouseLeave);
    };
  }, []);

  const scrollToGenerator = () => {
    generatorRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleDownload = () => {
    // ... download logic remains the same
  };

  const handleSubmit = async (e) => {
    // ... submit logic remains the same
  };

  const checkStatus = async () => {
    // ... check status logic remains the same
  };

  useEffect(() => {
    // ... polling logic remains the same
  }, [taskId]);

  return (
    <div className="App">
      <button onClick={toggleTheme} className="theme-toggle-btn">
        {theme === 'light' ? <FaMoon /> : <FaSun />}
      </button>

      <div className="hero-section">
