import React, { useState, useEffect, useRef } from 'react';
import './App.css';

// The URL of your backend API. Change this if your backend is deployed elsewhere.
const API_URL = 'http://localhost:8000'; 

function App() {
  const [repoUrl, setRepoUrl] = useState('');
  const [taskId, setTaskId] = useState(null);
  const [status, setStatus] = useState('Idle');
  const [result, setResult] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Use a ref to hold the interval ID
  const intervalRef = useRef(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setResult('');
    setTaskId(null);
    setIsLoading(true);
    setStatus('Submitting job...');

    try {
      const response = await fetch(`${API_URL}/generate`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ repo_url: repoUrl }),
      });

      if (!response.ok) {
        throw new Error('Failed to start the generation job.');
      }

      const data = await response.json();
      setTaskId(data.task_id);
      setStatus('Job submitted. Waiting for progress...');
    } catch (err) {
      setError(err.message);
      setIsLoading(false);
      setStatus('Idle');
    }
  };

  const checkStatus = async () => {
    if (!taskId) return;

    try {
      const response = await fetch(`${API_URL}/status/${taskId}`);
      const data = await response.json();

      setStatus(`Job Status: ${data.status}`);

      if (data.status === 'SUCCESS') {
        setResult(data.result);
        setIsLoading(false);
        clearInterval(intervalRef.current); // Stop polling
      } else if (data.status === 'FAILURE') {
        setError('Job failed. Please check the backend logs.');
        setIsLoading(false);
        clearInterval(intervalRef.current); // Stop polling
      }
    } catch (err) {
      setError('Failed to fetch status.');
      setIsLoading(false);
      clearInterval(intervalref.current); // Stop polling on error
    }
  };

  // useEffect hook to start polling when a taskId is available
  useEffect(() => {
    if (taskId) {
      intervalRef.current = setInterval(checkStatus, 3000); // Poll every 3 seconds
    }
    // Cleanup function to clear the interval when the component unmounts
    return () => clearInterval(intervalRef.current);
  }, [taskId]);

  return (
    <div className="App">
      <header className="App-header">
        <h1>RAG-Based README Generator</h1>
        <p>Enter a public GitHub repository URL to generate a README.md file.</p>
      </header>
      <main>
        <form onSubmit={handleSubmit} className="form-container">
          <input
            type="url"
            value={repoUrl}
            onChange={(e) => setRepoUrl(e.target.value)}
            placeholder="https://github.com/user/repo"
            required
            disabled={isLoading}
          />
          <button type="submit" disabled={isLoading}>
            {isLoading ? 'Generating...' : 'Generate README'}
          </button>
        </form>

        <div className="status-container">
          <h3>Status</h3>
          <p className="status-text">{status}</p>
          {error && <p className="error-text">Error: {error}</p>}
        </div>

        {result && (
          <div className="result-container">
            <h3>Generated README.md</h3>
            <textarea readOnly value={result}></textarea>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
