import React, { useState, useEffect, useRef } from 'react';
import './App.css';

const API_URL = 'http://localhost:8000'; 

function App() {
  const [repoUrl, setRepoUrl] = useState('');
  const [taskId, setTaskId] = useState(null);
  const [status, setStatus] = useState('Idle');
  const [result, setResult] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const intervalRef = useRef(null);

  // --- NEW: Function to handle the download ---
  const handleDownload = () => {
    // Create a new Blob object from the README content
    const blob = new Blob([result], { type: 'text/markdown' });

    // Create a temporary URL for the blob
    const url = URL.createObjectURL(blob);

    // Create a temporary anchor element and set its attributes
    const link = document.createElement('a');
    link.href = url;
    link.download = 'README.md'; // This is the name of the downloaded file

    // Append the link to the body, click it, and then remove it
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);

    // Clean up the temporary URL
    URL.revokeObjectURL(url);
  };
  // --- END of new function ---

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setResult('');
    setTaskId(null);
    setIsLoading(true);
    setStatus('Submitting job...');

    try {
      const response = await fetch(`${API_URL}/generate`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ repo_url: repoUrl }),
      });

      if (!response.ok) {
        throw new Error('Failed to start the generation job.');
      }

      const data = await response.json();
      setTaskId(data.task_id);
      setStatus('Job submitted. Waiting for progress...');
    } catch (err) {
      setError(err.message);
      setIsLoading(false);
      setStatus('Idle');
    }
  };

  const checkStatus = async () => {
    if (!taskId) return;

    try {
      const response = await fetch(`${API_URL}/status/${taskId}`);
      const data = await response.json();

      setStatus(`Job Status: ${data.status}`);

      if (data.status === 'SUCCESS') {
        setResult(data.result);
        setIsLoading(false);
        clearInterval(intervalRef.current);
      } else if (data.status === 'FAILURE') {
        setError('Job failed. Please check the backend logs or the result below.');
        // Display the error traceback in the result box
        setResult(data.result); 
        setIsLoading(false);
        clearInterval(intervalRef.current);
      }
    } catch (err) {
      setError('Failed to fetch status.');
      setIsLoading(false);
      clearInterval(intervalRef.current);
    }
  };

  useEffect(() => {
    if (taskId) {
      intervalRef.current = setInterval(checkStatus, 3000);
    }
    return () => clearInterval(intervalRef.current);
  }, [taskId]);

  return (
    <div className="App">
      <header className="App-header">
        <h1>RAG-Based README Generator üìù</h1>
        <p>Enter a public GitHub repository URL to generate a README.md file.</p>
      </header>
      <main>
        <form onSubmit={handleSubmit} className="form-container">
          <input
            type="url"
            value={repoUrl}
            onChange={(e) => setRepoUrl(e.target.value)}
            placeholder="https://github.com/user/repo"
            required
            disabled={isLoading}
          />
          <button type="submit" disabled={isLoading}>
            {isLoading ? 'Generating...' : 'Generate README'}
          </button>
        </form>

        <div className="status-container">
          <h3>Status</h3>
          <p className="status-text">{status}</p>
          {error && <p className="error-text">Error: {error}</p>}
        </div>

        {result && (
          <div className="result-container">
            <div className="result-header">
              <h3>Generated Content</h3>
              {/* --- NEW: The Download Button --- */}
              {/* It only shows if the task was a success and not an error */}
              {!result.startsWith("TASK FAILED:") && (
                <button onClick={handleDownload} className="download-btn">
                  Download README.md
                </button>
              )}
              {/* --- END of new button --- */}
            </div>
            <textarea readOnly value={result}></textarea>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
